import React, { useRef } from 'react';
import './contact.css';
import { Button } from 'react-bootstrap';
import emailjs from '@emailjs/browser';
function Contact() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm(
      'YOUR_SERVICE_ID',
      'YOUR_TEMPLATE_ID',
      form.current,
      'YOUR_USER_ID'
    )
      .then((result) => {
        console.log(result.text);
      }, (error) => {
        console.log(error.text);
      });
  };
      
  return (
    <div className='contact'>
      <section className="contact_section">
        <div className='contact_section_title'>
          <h3 className="section__subtitle">Get in <span>Touch</span></h3>
          <h2 className="section__title">Contact Me</h2>
        </div>

        <div className="contact__container container">
          <form onSubmit={sendEmail} className="contact__form" id="contact__form">
            <div className="contact__group">
              <div className='contact__group__input'>
                <input
                  type="text"
                  id="user_name"
                  name="user_name"
                  required
                  placeholder="Enter Your Name"
                  className="contact__input"
                />
                <input
                  type="email"
                  id="user_email"
                  name="user_email"
                  required
                  placeholder="Enter Your Email"
                  className="contact__input"
                />
              </div>
              <input
                type="text"
                id="user_phone"
                name="user_phone"
                required
                placeholder="Enter Your Phone Number"
                className="contact__input"
              />
            </div>

            <textarea
              id="message"
              name="user_message"
              className="contact__input"
              required
              placeholder="Enter Your Message"
            ></textarea>
            <p className="contact__message" id="contact_message">Message</p>
            <Button type="submit" className="contact__button py-2 px-3 btn btn-success mb-3">
              Send Message
            </Button>
          </form>
        </div>
      </section>
    </div>
  );
}

export default Contact;
