import React from 'react';
import { Card, Col, Container, Image, ListGroup, Row } from 'react-bootstrap';
import { useParams, Link } from 'react-router-dom';
import { replace } from 'react-string-replace';
import '../projects/projects.css';
import projects from '../data';

function ProjectDetails() {
  const { id } = useParams();
  console.log(id);

  const project = projects.find((project) => project.id === parseInt(id));

  if (!project) {
    return <div>Project not found</div>;
  }

  const { title, category, description, image, Link_Url, Used_Tools, features_in_website } = project;

  const replacedUsedTools = replace(Used_Tools, /\n/g, '<br>');

  return (
    <Container>
      <Link className="btn btn-success my-3" to="/projects">
        Go Back
      </Link>
      <Row>
        <Col md={6}>
          <Image src={image} alt="image-project" fluid />
        </Col>
        <Col md={6}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h4>Title: {title}</h4>
            </ListGroup.Item>
            <ListGroup.Item>
              <h5>Category: {category}</h5>
            </ListGroup.Item>
            <ListGroup.Item>
              Used Tools: <span dangerouslySetInnerHTML={{ __html: replacedUsedTools }} />
            </ListGroup.Item>
            <ListGroup.Item>Description: {description}</ListGroup.Item>
            <ListGroup.Item>
              Live Demo: <a href={Link_Url}>{Link_Url}</a>
            </ListGroup.Item>
            <ListGroup.Item>Features in Website: {features_in_website}</ListGroup.Item>
          </ListGroup>
        </Col>
      </Row>
      <Row className="mt-3">
        <Col>
          <div className="group-buttons">
            <Link className="btn btn-success m-3" to="/" variant="success">
              Go to Main Page
            </Link>
            <Link className="btn btn-success" to="/projects" variant="success">
              Go to Projects Page
            </Link>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default ProjectDetails;
